import styles from './InfoPanel.module.css'
import AccentImage from './AccentImage.js'
import React from 'react';

export default function InfoPanel() {

    /*  AccentImage takes props { path , position , size }
        { path } is the relative path to the static file in the public folder ( ex. /Kytra_00.png )
        { position } is either 'left' or 'right' which moves the image to the respective side of the InfoPanel
        { size } is one of the following: 'large_img' | 'med_img' | 'small_img' ( 400px, 250px, 150px - respectively)
     */
    return (
        <div className={`${styles.ks_panel} ${styles.row}`}>
            <AccentImage path={'/Kytra_00.png'} position={'right'} size={'med_img'}/>
        </div>
    )
}
